result=jiexi[num1==""?1:num1-1]+baseUrl
}else if(baseUrl.match(/bilibili.com/)){
result=jiexi[num2==""?1:num2-1]+baseUrl
}else if(baseUrl.match(/52gen/)){
	result=baseUrl.replace(/\/play.*/,'')+result
