	    str=result.match(/var .*="(.*)";/)[1]
	    key=result.match(/parse\("(.*)"\);var iv=/)[1]
      result=java.aesDecodeToString(str,key,"AES/CBC/PKCS7Padding","1234567890983456")
      result=result.match(/url: "(.*)",type:/)[1]
      if(result.match(/aliyundrive/)){
result="https://hll.aliyundrive.asia/oj/?vn="+result
	    }
   }
}else if(baseUrl.match(/voflix/)){
header={"headers":{"referer":"https://www.voflix.com/"}}
	result=result+","+JSON.stringify(header)
}

if(book.durChapterIndex==chapter.index){
